SELECT count(DISTINCT ui.user_info_id)
FROM user_info ui
LEFT JOIN user u ON ui.user_id = u.user_id
LEFT JOIN fall_ill fi ON ui.user_id = fi.user_id
LEFT JOIN urine_result ur ON ui.user_id = ur.user_id
LEFT JOIN urine_abnormal ua ON ur.urine_result_id = ua.urine_result_id
LEFT JOIN blood_pressure bp ON ui.user_id = bp.user_id
LEFT JOIN urine_protein urp ON ui.user_id = urp.user_id
LEFT JOIN blood_creatinine bc ON ui.user_id = bc.user_id

  u.state = '1'

    AND ui.province_id = #{parameters.province}

      AND ui.city_id = #{parameters.city}

        AND ui.area_id = #{parameters.area}




    AND ui.sex = #{parameters.sex}


    AND ui.birth <= #{parameters.minAge}


    AND ui.birth >= #{parameters.maxAge}


    AND fi.disease_id = #{parameters.diseaseId}


    AND fi.hospital_id = #{parameters.hospitalId}



      AND (ua.abnormal_acr = 1
      OR ua.abnormal_leu = 1
      OR ua.abnormal_nit = 1
      OR ua.abnormal_ubg = 1
      OR ua.abnormal_pro = 1
      OR ua.abnormal_ph = 1
      OR ua.abnormal_bld = 1
      OR ua.abnormal_sg = 1
      OR ua.abnormal_ket = 1
      OR ua.abnormal_bil = 1
      OR ua.abnormal_glu = 1
      OR ua.abnormal_vc = 1
      OR ua.abnormal_ma = 1
      OR ua.abnormal_cre = 1
      OR ua.abnormal_ca = 1)


      AND ur.inspect_time >= #{parameters.minTime}


      AND ur.inspect_time <= #{parameters.maxTime}


      AND ua.abnormal_acr = 1


      AND ua.abnormal_leu = 1


      AND ua.abnormal_nit = 1


      AND ua.abnormal_ubg = 1


      AND ua.abnormal_pro = 1


      AND ua.abnormal_ph = 1


      AND ua.abnormal_bld = 1


      AND ua.abnormal_sg = 1


      AND ua.abnormal_ket = 1


      AND ua.abnormal_bil = 1


      AND ua.abnormal_glu = 1


      AND ua.abnormal_vc = 1


      AND ua.abnormal_ma = 1


      AND ua.abnormal_cre = 1


      AND ua.abnormal_ca = 1




      AND bp.abnormal = #{parameters.abnormal}


      AND bp.create_time >= #{parameters.minTime}


      AND bp.create_time <= #{parameters.maxTime}




      AND bc.abnormal = #{parameters.abnormal}


      AND bc.create_time >= #{parameters.minTime}


      AND bc.create_time <= #{parameters.maxTime}




      AND urp.abnormal = #{parameters.abnormal}


      AND urp.create_time >= #{parameters.minTime}


      AND urp.create_time <= #{parameters.maxTime}

SELECT d.disease_name disease ,ui.*
,(SELECT SUM(abnormal_acr+abnormal_leu+abnormal_nit+abnormal_ubg+ abnormal_pro+abnormal_ph+abnormal_bld+abnormal_sg+abnormal_ket+abnormal_bil+ abnormal_glu+abnormal_vc+abnormal_ma+abnormal_cre+abnormal_ca) FROM urine_result ur1 LEFT JOIN urine_abnormal ua1 ON ur1.urine_result_id = ua1.urine_result_id WHERE ur1.user_id = ui.user_id) inspectSum , (SELECT COUNT(1) FROM urine_result ur1 LEFT JOIN urine_abnormal ua1 ON ur1.urine_result_id = ua1.urine_result_id WHERE ur1.user_id = ui.user_id) abnormalSum
,(SELECT COUNT(1) FROM blood_pressure WHERE user_id = ui.user_id) inspectSum , (SELECT COUNT(1) FROM blood_pressure WHERE user_id = ui.user_id AND abnormal = '0') abnormalSum
,(SELECT COUNT(1) FROM blood_creatinine WHERE user_id = ui.user_id) inspectSum , (SELECT COUNT(1) FROM blood_creatinine WHERE user_id = ui.user_id AND abnormal = '0') abnormalSum
,(SELECT COUNT(1) FROM urine_protein WHERE user_id = ui.user_id) inspectSum , (SELECT COUNT(1) FROM urine_protein WHERE user_id = ui.user_id AND abnormal = '0') abnormalSum
FROM user_info ui LEFT JOIN user u ON ui.user_id = u.user_id LEFT JOIN (SELECT * FROM fall_ill WHERE add_time IN (SELECT max(add_time) FROM fall_ill GROUP BY user_id)) fi ON ui.user_id = fi.user_id LEFT JOIN disease d ON fi.disease_id = d.disease_id LEFT JOIN urine_result ur ON ui.user_id = ur.user_id LEFT JOIN urine_abnormal ua ON ur.urine_result_id = ua.urine_result_id LEFT JOIN blood_pressure bp ON ui.user_id = bp.user_id LEFT JOIN urine_protein urp ON ui.user_id = urp.user_id LEFT JOIN blood_creatinine bc ON ui.user_id = bc.user_id LEFT JOIN fall_ill fil ON ui.user_id = fil.user_id
u.state = '1'
AND ui.province_id = #{parameters.province}
AND ui.city_id = #{parameters.city}
AND ui.area_id = #{parameters.area}
AND ui.sex = #{parameters.sex}
AND ui.age >= #{parameters.minAge}
AND ui.age <= #{parameters.maxAge}
AND fil.disease_id = #{parameters.diseaseId}
AND fil.hospital_id = #{parameters.hospitalId}
AND (ua.abnormal_acr = 1 OR ua.abnormal_leu = 1 OR ua.abnormal_nit = 1 OR ua.abnormal_ubg = 1 OR ua.abnormal_pro = 1 OR ua.abnormal_ph = 1 OR ua.abnormal_bld = 1 OR ua.abnormal_sg = 1 OR ua.abnormal_ket = 1 OR ua.abnormal_bil = 1 OR ua.abnormal_glu = 1 OR ua.abnormal_vc = 1 OR ua.abnormal_ma = 1 OR ua.abnormal_cre = 1 OR ua.abnormal_ca = 1)
AND ur.inspect_time >= #{parameters.minTime}
AND ur.inspect_time <= #{parameters.maxTime}
AND ua.abnormal_acr = 1
AND ua.abnormal_leu = 1
AND ua.abnormal_nit = 1
AND ua.abnormal_ubg = 1
AND ua.abnormal_pro = 1
AND ua.abnormal_ph = 1
AND ua.abnormal_bld = 1
AND ua.abnormal_sg = 1
AND ua.abnormal_ket = 1
AND ua.abnormal_bil = 1
AND ua.abnormal_glu = 1
AND ua.abnormal_vc = 1
AND ua.abnormal_ma = 1
AND ua.abnormal_cre = 1
AND ua.abnormal_ca = 1
AND bp.abnormal = #{parameters.abnormal}
AND bp.create_time >= #{parameters.minTime}
AND bp.create_time <= #{parameters.maxTime}
AND bc.abnormal = #{parameters.abnormal}
AND bc.create_time >= #{parameters.minTime}
AND bc.create_time <= #{parameters.maxTime}
AND urp.abnormal = #{parameters.abnormal}
AND urp.create_time >= #{parameters.minTime}
AND urp.create_time <= #{parameters.maxTime}
GROUP BY u.user_id ORDER BY ui.user_info_id LIMIT #{minRow},#{pageSize}

