SELECT count(DISTINCT ui.user_id)
FROM user_info ui
LEFT JOIN user u ON ui.user_id = u.user_id
LEFT JOIN user_equipment ue ON ui.user_id = ue.user_id

  u.state = '1'

    AND ui.province_id = #{parameters.province}

      AND ui.city_id = #{parameters.city}

        AND ui.area_id = #{parameters.area}




    AND ui.sex = #{parameters.sex}


    AND ui.age >= #{parameters.minAge}


    AND ui.age <= #{parameters.maxAge}


    AND ue.start_time >= #{parameters.minTime}
    AND ue.end_time = '2000-01-01 00:00:00'


    AND ue.start_time <= #{parameters.maxTime}
    AND ue.end_time = '2000-01-01 00:00:00'


    AND ue.equipment_type = #{parameters.equipmentType}

      AND ue.equipment_model = #{parameters.model}

    AND ue.end_time = '2000-01-01 00:00:00'

SELECT ui.* , ue.* , r1.name province , r2.name city ,r3.name area
FROM user_info ui
LEFT JOIN user u ON ui.user_id = u.user_id
LEFT JOIN (SELECT * FROM user_equipment WHERE end_time = '2000-01-01 00:00:00') ue
ON ui.user_id = ue.user_id
LEFT JOIN user_equipment ue1 ON ui.user_id = ue1.user_id
LEFT JOIN region_2017 r1 ON ui.province_id = r1.id
LEFT JOIN region_2017 r2 ON ui.city_id = r2.id
LEFT JOIN region_2017 r3 ON ui.area_id = r3.id

  u.state = '1'

    AND ui.province_id = #{parameters.province}

      AND ui.city_id = #{parameters.city}

        AND ui.area_id = #{parameters.area}




    AND ui.sex = #{parameters.sex}


    AND ui.birth <= #{parameters.minAge}


    AND ui.birth >= #{parameters.maxAge}


    AND ue1.start_time >= #{parameters.minTime}
    AND ue1.end_time = '2000-01-01 00:00:00'


    AND ue1.start_time <= #{parameters.maxTime}
    AND ue1.end_time = '2000-01-01 00:00:00'


    AND ue1.equipment_type = #{parameters.equipmentType}

      AND ue1.equipment_model = #{parameters.model}

    AND ue1.end_time = '2000-01-01 00:00:00'


GROUP BY ui.user_id
ORDER BY ui.user_info_id
LIMIT #{minRow},#{pageSize}

